        var sendPv = (function (smartyData) {
            return function () {
                var logInfoExt = isStatic
                    ? data && data.logInfoExt
                    : smartyData.dynamicData.logInfoExt;
                logInfoExt = logInfoExt && Object.prototype.toString.call(logInfoExt) !== '[object Array]'
                    ? logInfoExt
