			var getBaiduidUrl = '//nuomipassport.baidu.com/getbdid';
			var retryCount = 1;
			getBaiduid();

			function getBaiduid(){
				$.ajax(getBaiduidUrl, {
					dataType : 'jsonp',
					success : function(json){
						if(json.errno == 0){
							var baiduid = json.data.baiduid;
							if(baiduid){
								Cookie.setRaw('BAIDUID', baiduid, 365);
							}
							// 不管有没有baiduid，都发送pv
							sendPv();
						}else{
							if(retryCount--){
								getBaiduid();
							}else{
								// 最后一次也获取失败
								sendPv();
							}
						}
					}
				});
			}
		}

